import{_ as s,o as t,c as a,k as e,a as o}from"./chunks/framework.cc795e4d.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/ADA 奖项/国家科学和卫生保健成就奖/糖尿病杰出教育者奖.md","filePath":"docs/ADA 奖项/国家科学和卫生保健成就奖/糖尿病杰出教育者奖.md"}'),c={name:"docs/ADA 奖项/国家科学和卫生保健成就奖/糖尿病杰出教育者奖.md"},n=e("h3",{id:"糖尿病杰出教育者奖",tabindex:"-1"},[o("糖尿病杰出教育者奖 "),e("a",{class:"header-anchor",href:"#糖尿病杰出教育者奖","aria-label":'Permalink to "糖尿病杰出教育者奖"'},"​")],-1),i=e("p",null,"芝加哥大学医学科夫勒糖尿病中心 Amy Hess-Fischl 教授",-1),_=e("blockquote",null,[e("p",null,"糖尿病杰出教育者奖的设立旨在褒奖在糖尿病教育、技术培训等领域做出重大贡献的专家。")],-1),l=e("p",null,"当地时间6月4日上午，第82届ADA杰出的糖尿病教育者奖得主—来自芝加哥大学医学Kovler糖尿病中心的Amy HessFischl教授做了题为“We Are the Champions, My Friends— Adventures in Diabetes Care and Education”的演讲。",-1),r=e("p",null,"Amy Hess-Fischl教授超过20年的工作都是为糖尿病儿童、青少年、年轻人和成年人及其家人提供相关教育、培训与支持。在多年的工作中Amy Hess-Fischl与她的被教育辅导的群体成为了朋友，她认为在糖尿病护理教育与支持中每一个参与者都很重要，包括医生、护士、患者、家属等等。有效的“糖尿病自我管理教育与支持（DSMES）”有许多益处：让患者了解到糖尿病相关的重要常识并理解治疗计划，减少紧急事件和降低住院率、减少低血糖和全因死亡，促进生活方式的改变，帮助保持或降低体重，增强患者的自我效能感、减少抑郁情绪，从而提高生活质量。截止到2021年美国超过70万患者加入到糖尿病自我管理教育项目，而下列临床情况改善的数据体现了加入糖尿病教育指导起到的重要作用。2021年ADA发布的数据表明，77.4%接受DSMES的患者实现HbA1c下降，平均下降2%；接受DSMES的患者急诊就诊次数减少82%，住院次数减少84.4%，67.9%的患者实现体重下降。并非每例糖尿病患者都遵循相同的路径，但所有人都可以从DSMES中受益（图1）。",-1),d=e("p",null,[e("img",{src:"https://p.ipic.vip/ssacpa.png",alt:"image-20231005142640755"})],-1),p=[n,i,_,l,r,d];function h(m,A,u,D,f,S){return t(),a("div",null,p)}const k=s(c,[["render",h]]);export{b as __pageData,k as default};
