import{_,o as t,c as a,k as e,a as s}from"./chunks/framework.cc795e4d.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/治疗进展/肠促激素专题/应用新型GIPGLP-1胰高血糖素三受体激动剂LY3437943-LY-12周可改善T2DM患者血糖及体重.md","filePath":"docs/治疗进展/肠促激素专题/应用新型GIPGLP-1胰高血糖素三受体激动剂LY3437943-LY-12周可改善T2DM患者血糖及体重.md"}'),o={name:"docs/治疗进展/肠促激素专题/应用新型GIPGLP-1胰高血糖素三受体激动剂LY3437943-LY-12周可改善T2DM患者血糖及体重.md"},c=e("h3",{id:"应用新型gip-glp-1-胰高血糖素三受体激动剂ly3437943-ly-12周可改善t2dm患者血糖及体重",tabindex:"-1"},[s("应用新型GIP/GLP-1/胰高血糖素三受体激动剂LY3437943(LY) 12周可改善T2DM患者血糖及体重 "),e("a",{class:"header-anchor",href:"#应用新型gip-glp-1-胰高血糖素三受体激动剂ly3437943-ly-12周可改善t2dm患者血糖及体重","aria-label":'Permalink to "应用新型GIP/GLP-1/胰高血糖素三受体激动剂LY3437943(LY) 12周可改善T2DM患者血糖及体重"'},"​")],-1),L=e("p",null,"礼来公司Shweta Urva博士",-1),n=e("p",null,"LY是一种新型葡萄糖依赖性促胰岛素多肽(GIP)、胰高糖素样肽-1(GLP-1)和胰高血糖素三受体强效激动剂，正在开发用于治疗多种代谢紊乱。本随机、双盲、安慰剂对照、1期研究的主要目的是评估LY治疗12周时在2型糖尿病(T2D)患者中的安全性和耐受性疗效。72例患者被随机(9:3:1)分配至LY的5个剂量（0.5、1.5、3、3/6、3/6/9/12 mg）组、安慰剂组和度拉糖肽1.5 mg组，在LY队列内剂量最高的2个组中采取剂量递增给药至目标剂量。结果显示，治疗中最常见的不良事件(AE)为胃肠道事件（包括恶心和腹泻），严重程度大多为轻度。第12周时，与安慰剂组相比，LY组的平均收缩压和舒张压较基线降低；相较于安慰剂组， LY组和度拉糖肽组中患者脉搏和心率较基线升高。第12周时，所有组的平均HbA1c均较基线降低，LY 3/6mg组经安慰剂校正后的降幅高达1.56%；LY组观察到平均体重呈剂量依赖性降低， LY 3/6/9/12mg组经安慰剂校正后降幅达8.96 kg（P＜ 0.05）（图 1）。LY3437943的安全性和耐受性特征与其他肠促激素相似。本研究中观察到血糖降低和体重减轻疗效，突显了LY与现有疗法相比在T2D和肥胖治疗中可提供额外获益的潜力。",-1),l=e("p",null,[e("img",{src:"https://p.ipic.vip/dflxw2.png",alt:"image-20231005151746476"})],-1),r=[c,L,n,l];function d(i,p,Y,m,P,h){return t(),a("div",null,r)}const f=_(o,[["render",d]]);export{g as __pageData,f as default};
