import{_ as e,o as t,c as a,k as _,a as s}from"./chunks/framework.cc795e4d.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/治疗进展/肠促激素专题/度拉糖肽治疗2型糖尿病-T2D-青少年患者-AWARD-PEDS随机-安慰剂对照试验的结果.md","filePath":"docs/治疗进展/肠促激素专题/度拉糖肽治疗2型糖尿病-T2D-青少年患者-AWARD-PEDS随机-安慰剂对照试验的结果.md"}'),c={name:"docs/治疗进展/肠促激素专题/度拉糖肽治疗2型糖尿病-T2D-青少年患者-AWARD-PEDS随机-安慰剂对照试验的结果.md"},o=_("h3",{id:"度拉糖肽治疗2型糖尿病-t2d-青少年患者—award-peds随机、安慰剂对照试验的结果",tabindex:"-1"},[s("度拉糖肽治疗2型糖尿病(T2D)青少年患者—AWARD-PEDS随机、安慰剂对照试验的结果 "),_("a",{class:"header-anchor",href:"#度拉糖肽治疗2型糖尿病-t2d-青少年患者—award-peds随机、安慰剂对照试验的结果","aria-label":'Permalink to "度拉糖肽治疗2型糖尿病(T2D)青少年患者—AWARD-PEDS随机、安慰剂对照试验的结果"'},"​")],-1),D=_("p",null,"匹兹堡儿童医院 Silva A Arslanian MD",-1),n=_("p",null,"AWARD-PEDS是一项在仅接受生活方式治疗或接受稳定二甲双胍伴或不伴基础胰岛素治疗的青年T2D患者（10至18岁）中评估度拉糖肽(DU)的疗效和安全性的3期试验。受试者（平均年龄14.5岁；平均BMI 34.1 kg/m2）随机接受安慰剂(N = 51)、 DU 0.75 mg(N = 51)或DU 1.5 mg(N = 52)。主要目的是证明在第 26周时DU与安慰剂相比降HbA1c水平的优效性。结果显示DU在改善血糖控制（HbA1c较基线变化、HbA1c < 7%达标率和空腹血糖较基线变化）方面均优于安慰剂（图1），未观察到DU对 BMI变化的影响(p = 0.776)。DU组常见GI不良事件的发生率高于安慰剂组[恶心(14.6%vs. 7.8%)、呕吐(15.5%vs. 3.9%)、腹泻(18.4%vs. 13.7%)]，但与成人中观察到的发生率相当。在接受或未接受二甲双胍和/或基础胰岛素治疗但血糖控制不佳的青年 T2D患者中，每周一次DU 0.75 mg或1.5 mg在26周内改善血糖控制方面优于安慰剂，且对BMI无影响，安全性特征与在成人研究中一致。",-1),d=_("p",null,[_("img",{src:"https://p.ipic.vip/6cjc09.png",alt:"image-20231005151327581"})],-1),r=[o,D,n,d];function i(l,p,A,m,h,P){return t(),a("div",null,r)}const S=e(c,[["render",i]]);export{g as __pageData,S as default};
