import{_ as e,o as s,c as _,k as t}from"./chunks/framework.cc795e4d.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/糖尿病管理/并发症管理/糖尿病肾病/共识-ADA和KDIGO关于糖尿病和CKD管理的新联合声明.md","filePath":"docs/糖尿病管理/并发症管理/糖尿病肾病/共识-ADA和KDIGO关于糖尿病和CKD管理的新联合声明.md"}'),o={name:"docs/糖尿病管理/并发症管理/糖尿病肾病/共识-ADA和KDIGO关于糖尿病和CKD管理的新联合声明.md"},n=t("p",null,"共识-ADA和KDIGO关于糖尿病和CKD管理的新联合声明",-1),c=t("p",null,"哥本哈根大学临床医学系Peter Rossing MD DMSc",-1),i=t("p",null,"为了促进糖尿病患者的管理执行方针的统一，ADA和KDIGO代表发表联合声明，内容主要包括筛查与诊断、综合护理（包括目标、教育、自我管理、患者自我效能、多学科团队护理以及生活方式）、治疗目标及药物治疗。首先，目前对于糖尿病患者慢性肾脏疾病的筛查频率较低，只有50%的患者1年内进行过UACR和eGFR检查。在CKD患者中仅有34.9%的患者应用了ACE或ARB类药物，很重要的原因便是筛查不足，如果不能有效的发现慢性肾脏疾病，则无法开始相应的治疗。对于糖尿病患者CKD的筛查我们需要注意如下几点：1）何时为哪些患者筛查：1型糖尿病确诊满5年、2型糖尿病确诊之时起均应开始进行筛查；2）如何筛查：规律检测随机尿白蛋白-肌酐比值(ACR)和估计肾小球滤过率(eGFR)；3）获得阳性结果后如何做：评价可能的暂时性或假性原因，考虑使用 cyctatin C和肌酐更精确地估计GFR，仅持续性异常定义CKD，启动基于证据的治疗；4）如何确诊CKD：ACR持续≥ 30mg/g、 eGFR持续低于60mL/min/1.73m2、其他肾脏受损证据。也可以通过GFR和尿蛋白值来进行分层（图1），来判断后续管理方案。",-1),a=t("p",null,[t("img",{src:"https://p.ipic.vip/cb5j9z.png",alt:"image-20231005150554562"})],-1),p=t("p",null,"对于患有糖尿病和慢性肾疾病的患者应按照相应的流程进行管理：1）生活方式干预（健康饮食、运动锻炼、戒烟、体重管理）的同时启动药物治疗；2）一线药物包括SGLT2i、二甲双胍、 RAS抑制剂及他汀类药物；3）对血糖、蛋白尿、血压CVD防风险以及血脂做定期评估，如果具有额外风险则考虑增加相应的其他种类药物（如：GLP-1RA、MRA、非二氢砒啶类CCB、阿司匹林、 PCSK9i等)（图2）。",-1),D=t("p",null,[t("img",{src:"https://p.ipic.vip/x3dwfs.png",alt:"image-20231005150622421"})],-1),l=t("p",null,"指南强调了GCM 技术在改善糖尿病管理中的重要作用，并在临床用eGFR 和UACR 两个指标对CKD 进行监测，健康的生活方式和教育是一切治疗的基石。一线药物包括SGLT2i、二甲双胍、RAS 抑制剂及他汀类药物。",-1),d=[n,c,i,a,p,D,l];function r(A,C,m,K,G,R){return s(),_("div",null,d)}const g=e(o,[["render",r]]);export{u as __pageData,g as default};
