import{_,o as t,c as a,k as e,a as i}from"./chunks/framework.cc795e4d.js";const R=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/糖尿病管理/并发症管理/糖尿病肾病/在SURPASS-4研究中-Tirzepatide与甘精胰岛素100U-mL对2糖尿病受试者肾脏结局的影响-ADA大会主席的荐选摘要-.md","filePath":"docs/糖尿病管理/并发症管理/糖尿病肾病/在SURPASS-4研究中-Tirzepatide与甘精胰岛素100U-mL对2糖尿病受试者肾脏结局的影响-ADA大会主席的荐选摘要-.md"}'),s={name:"docs/糖尿病管理/并发症管理/糖尿病肾病/在SURPASS-4研究中-Tirzepatide与甘精胰岛素100U-mL对2糖尿病受试者肾脏结局的影响-ADA大会主席的荐选摘要-.md"},r=e("h3",{id:"在surpass-4-研究中-tirzepatide-与甘精胰岛素100-u-ml-对2-型糖尿病受试者肾脏结局的影响",tabindex:"-1"},[i("在SURPASS-4 研究中，Tirzepatide 与甘精胰岛素100 U/mL 对2 型糖尿病受试者肾脏结局的影响 "),e("a",{class:"header-anchor",href:"#在surpass-4-研究中-tirzepatide-与甘精胰岛素100-u-ml-对2-型糖尿病受试者肾脏结局的影响","aria-label":'Permalink to "在SURPASS-4 研究中，Tirzepatide 与甘精胰岛素100 U/mL 对2 型糖尿病受试者肾脏结局的影响"'},"​")],-1),d=e("p",null,"格罗宁根大学医学中心 Hiddo LHeerspink教授",-1),o=e("p",null,"Tirzepatide（TZP）是一种新型GIP/GLP-1受体激动剂。 SURPASS-4研究中，在口服降糖药治疗后血糖仍控制不佳，且心血管风险升高的2型糖尿病（T2D）患者中，其降低HbA1c水平的作用大于甘精胰岛素（iGlar）（中位研究持续时间为85周）。 SURPASS-4中的复合肾脏结局定义为：终点1（eGFR[CKD-EPI]相对于基线下降≥ 40%、肾死亡、进展为ESRD、新发大量白蛋白尿）和终点2（终点1除外新发大量白蛋白尿）。基线时，受试者（N=1995，平均年龄：63.6岁，HbA1c：8.5%）的平均 eGFR为81.3 mL/min/1.73 m2，17%的受试者的eGFR<60 mL/ min/1.73 m2，28%的受试者为微量白蛋白尿（UACR 30-300 mg/g），8%的受试者为大量白蛋白尿（UACR>300 mg/g）。在为期104周的随访期间，与iGlar相比，TZP组的肾脏结局事件显著更少，尤其是新发大量白蛋白尿明显更少（图1）。",-1),c=e("p",null,[e("img",{src:"https://p.ipic.vip/dcu9gb.png",alt:"image-20231005150815158"})],-1),n=[r,d,o,c];function p(m,S,l,A,P,U){return t(),a("div",null,n)}const u=_(s,[["render",p]]);export{R as __pageData,u as default};
